import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-moment-form',
  templateUrl: './moment-form.component.html',
  styleUrls: ['./moment-form.component.css']
})
export class MomentFormComponent implements OnInit{
  @Input() btnText!: string

  momentForm!: FormGroup

  constructor() {}

  ngOnInit(): void {
      this.momentForm = new FormGroup({
        id: new FormControl(''),
        title: new FormControl('', [Validators.required]),
        description: new FormControl('', [Validators.required]),
        image: new FormControl(''),
      })
  }

  get title() {
    return this.momentForm.get('title')!;
  }

  get description() {
    return this.momentForm.get('description')!;
  }

  submit() {
    console.log("Enviou o formulário")
  }
}
<form (ngSubmit)="submit()" [formGroup]="momentForm" #formDir="ngForm">
  <div class="form-group">
    <label for="title">Título:</label>
    <input
      type="text" 
      placeholder="Coloque um título" 
      formControlName="title" 
      required
    />
  <div *ngIf="title.invalid && formDir.submitted" class="validation-error">
    <p *ngIf="title.erros?.['required']">O título é obrigatório</p>
  </div>
  </div>
  <div class="form-group">
    <label for="description">Descrição:</label>
    <textarea placeholder="O que aconteceu lá?" required></textarea>
  </div>
  <div class="form-group">
    <label for="image">Coloque a foto que mais marcou:</label>
    <input type="file">
  </div>
  <input type="submit" value="{{btnText}}">
</form>
