import { Component, Input, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-moment-form',
  templateUrl: './moment-form.component.html',
  styleUrls: ['./moment-form.component.css']
})
export class MomentFormComponent implements OnInit{
  @Input() btnText!: string

  momentForm!: FormGroup

  constructor() {}

  ngOnInit(): void {

  }

  submit() {
    console.log("Enviou o formulário")
  }
}
<form (ngSubmit)="submit()" [formGroup]="momentForm">
  <div class="form-group">
    <label for="title">Título:</label>
    <input type="text" placeholder="Coloque um título" required>
  </div>
  <div class="form-group">
    <label for="description">Descrição:</label>
    <textarea placeholder="O que aconteceu lá?" required></textarea>
  </div>
  <div class="form-group">
    <label for="image">Coloque a foto que mais marcou:</label>
    <input type="file">
  </div>
  <input type="submit" value="{{btnText}}">
</form>
